import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaHome, FaShoppingCart, FaUser, FaFire, FaStar } from "react-icons/fa";

export default function Header() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [cartCount, setCartCount] = useState(0);
  const [pop, setPop] = useState(false);
  const [hot, setHot] = useState(false);

  // update cart count + animation
  useEffect(() => {
    const updateCart = () => {
      const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
      const count = storedCart.reduce((acc, item) => acc + (item.quantity || 1), 0);

      if (count > cartCount) {
        setPop(true);
        setHot(true); 
        setTimeout(() => setPop(false), 300);
        setTimeout(() => setHot(false), 1500);
      }

      setCartCount(count);
    };

    updateCart();
    window.addEventListener("storage", updateCart);
    return () => window.removeEventListener("storage", updateCart);
  }, [cartCount]);

  const logout = () => {
    localStorage.clear();
    navigate("/login");
  };

  const handleProtectedClick = (e, path) => {
    if (!user) {
      e.preventDefault();
      alert("Please login to view this page ðŸ”’");
    } else {
      navigate(path);
    }
  };

  return (
    <header style={styles.header}>
      {/* Logo / Top branding */}
      <div style={styles.logoContainer} onClick={(e) => handleProtectedClick(e, "/")}>
        <span style={styles.emoji}>ðŸ“š</span>
        <h2 style={styles.logo}>BookStore</h2>
        <span style={styles.topBadge}>
          <FaStar style={{ color: "#ffd700", marginRight: 4 }} />
          Trending
        </span>
      </div>

      {/* Navigation */}
      <nav style={styles.nav}>
        {/* Home */}
        <span onClick={(e) => handleProtectedClick(e, "/")} style={styles.navItem}>
          <FaHome style={{ marginRight: 6 }} /> Home
        </span>

        {/* Cart */}
        <span onClick={(e) => handleProtectedClick(e, "/cart")} style={{ ...styles.navItem, position: "relative" }}>
          <FaShoppingCart style={{ marginRight: 6 }} /> Cart
          {cartCount > 0 && (
            <span style={{
              ...styles.badge,
              transform: pop ? "scale(1.5)" : "scale(1)",
              backgroundColor: hot ? "#ff5722" : "#ff3b2e",
              boxShadow: hot ? "0 0 12px rgba(255,87,34,0.7)" : "none"
            }}>
              {cartCount}
              {hot && <FaFire style={{ marginLeft: 2, fontSize: 10 }} />}
            </span>
          )}
        </span>

        {/* User */}
        {user ? (
          <div style={styles.userSection}>
            <FaUser style={{ marginRight: 5 }} />
            Hello, {user.name || "User"}
            <button style={styles.logoutBtn} onClick={logout}>
              Logout
            </button>
          </div>
        ) : (
          <span onClick={() => navigate("/login")} style={styles.loginBtn}>
            <FaUser style={{ marginRight: 6 }} /> Login
          </span>
        )}
      </nav>
    </header>
  );
}

const styles = {
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "12px 30px",
    backgroundColor: "#ff6f61",
    color: "#fff",
    position: "sticky",
    top: 0,
    zIndex: 1000,
    boxShadow: "0 4px 15px rgba(0,0,0,0.15)",
  },
  logoContainer: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    cursor: "pointer",
    position: "relative",
  },
  emoji: {
    fontSize: "1.8rem",
    transform: "rotate(-10deg)",
  },
  logo: {
    fontSize: "1.6rem",
    fontWeight: 700,
    letterSpacing: "1px",
  },
  topBadge: {
    position: "absolute",
    top: "-10px",
    left: "100%",
    marginLeft: 6,
    backgroundColor: "#ffd700",
    color: "#333",
    fontWeight: 600,
    fontSize: "0.75rem",
    padding: "2px 6px",
    borderRadius: "6px",
    display: "flex",
    alignItems: "center",
    gap: "2px",
  },
  nav: {
    display: "flex",
    gap: "20px",
    alignItems: "center",
  },
  navItem: {
    cursor: "pointer",
    color: "#fff",
    fontWeight: 600,
    padding: "6px 12px",
    borderRadius: "8px",
    display: "flex",
    alignItems: "center",
    transition: "background 0.3s, transform 0.2s",
  },
  badge: {
    position: "absolute",
    top: "-6px",
    right: "-10px",
    width: "22px",
    height: "22px",
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "0.75rem",
    fontWeight: 700,
    color: "#fff",
    transition: "transform 0.3s, background 0.3s, box-shadow 0.3s",
  },
  userSection: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    backgroundColor: "#fff",
    color: "#ff6f61",
    padding: "6px 12px",
    borderRadius: "10px",
    fontWeight: 600,
  },
  logoutBtn: {
    backgroundColor: "#ff6f61",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    padding: "4px 10px",
    cursor: "pointer",
    fontWeight: 600,
    transition: "opacity 0.2s, transform 0.2s",
  },
  loginBtn: {
    cursor: "pointer",
    padding: "6px 15px",
    backgroundColor: "#fff",
    color: "#ff6f61",
    borderRadius: "8px",
    fontWeight: 600,
    display: "flex",
    alignItems: "center",
    transition: "opacity 0.3s, transform 0.2s",
  },
};
