import { useEffect, useState } from "react";
import BookCard from "../components/BookCard";
import api from "../services/api"; // Axios instance
import "../styles/main.css";

export default function Home() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    // Fetch available books from backend
    const fetchBooks = async () => {
      try {
        const res = await api.get("/books");
        setBooks(res.data);
      } catch (err) {
        console.error("Failed to fetch books:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  // Handle protected actions (like adding to cart)
  const handleProtectedClick = (action) => {
    if (!user) {
      alert("Please login to view this page");
    } else {
      action && action();
    }
  };

  return (
    <div className="home-page">
      {/* Banner */}
      <div className="home-banner">
        <div className="banner-overlay">
          <div className="banner-text">
            <h1>Welcome to Online Bookstore</h1>
            <p>Explore & Buy Your Favorite Books</p>
            <button
              className="shop-now-btn"
              onClick={() => handleProtectedClick(() => console.log("Go to books section"))}
            >
              Shop Now
            </button>
          </div>
        </div>
      </div>

      {/* Featured Books */}
      <div className="container">
        <h2 className="section-title">Featured Books</h2>

        {loading ? (
          <p style={{ textAlign: "center", margin: "20px 0" }}>Loading books...</p>
        ) : books.length === 0 ? (
          <p style={{ textAlign: "center", margin: "20px 0" }}>No books available at the moment ðŸ˜¢</p>
        ) : (
          <div className="books-grid">
            {books.map((book) => (
              <div
                key={book._id}
                onClick={() =>
                  handleProtectedClick(() => console.log(`View book ${book.title}`))
                }
              >
                <BookCard book={book} />
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
