import React from "react";
import { FaCartPlus } from "react-icons/fa";

export default function BookCard({ book }) {
  const addToCart = () => {
    const cart = JSON.parse(localStorage.getItem("cart")) || [];
    const existing = cart.find((item) => item._id === book._id);

    if (existing) {
      // Increase quantity if book already in cart
      existing.quantity += 1;
    } else {
      cart.push({ ...book, quantity: 1 });
    }

    localStorage.setItem("cart", JSON.stringify(cart));
    // Optional: trigger a custom event to update cart count in Header
    window.dispatchEvent(new Event("storage"));
    alert(`${book.title} added to cart! ðŸ›’`);
  };

  return (
    <div style={styles.card}>
      <div style={styles.imgContainer}>
        <img src={book.image} alt={book.title} style={styles.img} />
      </div>
      <div style={styles.info}>
        <h3 style={styles.title}>{book.title}</h3>
        <p style={styles.author}>by {book.author}</p>
        <p style={styles.price}>â‚¹{book.price}</p>
        <button style={styles.button} onClick={addToCart}>
          <FaCartPlus style={{ marginRight: 6 }} /> Add to Cart
        </button>
      </div>
    </div>
  );
}

const styles = {
  card: {
    border: "1px solid #eee",
    borderRadius: "10px",
    overflow: "hidden",
    backgroundColor: "#fff",
    boxShadow: "0 2px 10px rgba(0,0,0,0.05)",
    transition: "transform 0.3s, box-shadow 0.3s",
    cursor: "pointer",
  },
  imgContainer: {
    width: "100%",
    height: "250px",
    overflow: "hidden",
  },
  img: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
    transition: "transform 0.3s",
  },
  info: {
    padding: "15px",
    textAlign: "center",
  },
  title: {
    fontSize: "16px",
    fontWeight: 600,
    margin: "10px 0 5px",
  },
  author: {
    fontSize: "14px",
    color: "#777",
    marginBottom: "10px",
  },
  price: {
    fontSize: "16px",
    fontWeight: 600,
    color: "#ff6f61",
    marginBottom: "10px",
  },
  button: {
    padding: "10px 15px",
    backgroundColor: "#ff6f61",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    fontWeight: 600,
    cursor: "pointer",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    transition: "all 0.3s",
  },
};

// Hover effects
Object.assign(styles.card, {
  ":hover": {
    transform: "translateY(-5px)",
    boxShadow: "0 10px 20px rgba(0,0,0,0.1)",
  },
});

Object.assign(styles.img, {
  ":hover": {
    transform: "scale(1.05)",
  },
});

Object.assign(styles.button, {
  ":hover": {
    backgroundColor: "#ff3b2e",
  },
});
