import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaHome, FaShoppingCart, FaUser, FaFire, FaBookOpen, FaHeadset } from "react-icons/fa";

export default function Header() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [cartCount, setCartCount] = useState(0);
  const [pop, setPop] = useState(false);
  const [hot, setHot] = useState(false);

  useEffect(() => {
    const updateCart = () => {
      const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
      const count = storedCart.reduce((acc, item) => acc + (item.quantity || 1), 0);

      if (count > cartCount) {
        setPop(true);
        setHot(true);
        setTimeout(() => setPop(false), 300);
        setTimeout(() => setHot(false), 1500);
      }

      setCartCount(count);
    };

    updateCart();
    window.addEventListener("storage", updateCart);
    return () => window.removeEventListener("storage", updateCart);
  }, [cartCount]);

  const logout = () => {
    localStorage.clear();
    navigate("/login");
  };

  const handleProtectedClick = (e, path) => {
    if (!user) {
      e.preventDefault();
      alert("Please login to view this page");
    } else {
      navigate(path);
    }
  };

  return (
    <header style={styles.header}>
      {/* Top Bar */}
      <div style={styles.topBar}>
        <div>
          <FaHeadset style={{ marginRight: 6 }} /> Support: +91 12345 67890
        </div>
        <div style={{ display: "flex", gap: 20 }}>
          <span style={styles.topLink}>Offers</span>
          <span style={styles.topLink}>Trending</span>
          <span style={styles.topLink}>New Arrivals</span>
        </div>
      </div>

      {/* Main Navbar */}
      <div style={styles.navbar}>
        {/* Logo */}
        <h2 style={styles.logo} onClick={(e) => handleProtectedClick(e, "/")}>
          <FaBookOpen style={{ marginRight: 6 }} />
          ðŸ“š BookStore
        </h2>

        {/* Navigation */}
        <nav style={styles.nav}>
          <span
            onClick={(e) => handleProtectedClick(e, "/")}
            style={styles.navItem}
          >
            <FaHome style={{ marginRight: 6 }} />
            Home
          </span>

          <span
            onClick={(e) => handleProtectedClick(e, "/cart")}
            style={{ ...styles.navItem, position: "relative" }}
          >
            <FaShoppingCart style={{ marginRight: 6 }} />
            Cart
            {cartCount > 0 && (
              <span
                style={{
                  ...styles.badge,
                  transform: pop ? "scale(1.5)" : "scale(1)",
                  backgroundColor: hot ? "#ff5722" : "#ff3b2e",
                }}
              >
                {cartCount}
                {hot && <FaFire style={{ marginLeft: 2, fontSize: 10 }} />}
              </span>
            )}
          </span>

          {user ? (
            <div style={styles.userSection}>
              <FaUser style={{ marginRight: 5 }} />
              Hello, {user.name || "User"}
              <button style={styles.logoutBtn} onClick={logout}>
                Logout
              </button>
            </div>
          ) : (
            <span onClick={() => navigate("/login")} style={styles.loginBtn}>
              <FaUser style={{ marginRight: 6 }} />
              Login
            </span>
          )}
        </nav>
      </div>
    </header>
  );
}

const styles = {
  header: {
    width: "100%",
    top: 0,
    position: "sticky",
    zIndex: 999,
    fontFamily: "'Poppins', sans-serif",
  },
  topBar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "6px 30px",
    backgroundColor: "#ff7f7f",
    color: "#fff",
    fontSize: 12,
    fontWeight: 500,
  },
  topLink: {
    cursor: "pointer",
    transition: "color 0.3s",
  },
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "12px 30px",
    backgroundColor: "#ff6f61",
    color: "#fff",
    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
  },
  logo: {
    cursor: "pointer",
    fontSize: "1.8rem",
    fontWeight: 700,
    display: "flex",
    alignItems: "center",
    letterSpacing: 1,
  },
  nav: {
    display: "flex",
    gap: "20px",
    alignItems: "center",
  },
  navItem: {
    cursor: "pointer",
    color: "#fff",
    fontWeight: 600,
    padding: "6px 12px",
    borderRadius: "6px",
    display: "flex",
    alignItems: "center",
    transition: "all 0.3s",
  },
  badge: {
    position: "absolute",
    top: "-6px",
    right: "-10px",
    width: "22px",
    height: "22px",
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "0.75rem",
    fontWeight: 700,
    color: "#fff",
    transition: "transform 0.3s, background 0.3s",
  },
  userSection: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    backgroundColor: "#fff",
    color: "#ff6f61",
    padding: "6px 12px",
    borderRadius: "8px",
    fontWeight: 600,
    cursor: "pointer",
  },
  logoutBtn: {
    backgroundColor: "#ff6f61",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    padding: "4px 10px",
    cursor: "pointer",
    fontWeight: 600,
    marginLeft: 10,
    transition: "all 0.2s",
  },
  loginBtn: {
    cursor: "pointer",
    padding: "6px 15px",
    backgroundColor: "#fff",
    color: "#ff6f61",
    borderRadius: "8px",
    fontWeight: 600,
    display: "flex",
    alignItems: "center",
    transition: "all 0.3s",
  },
};
