import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import BookCard from "../components/BookCard.jsx";
import sampleBooks from "../data/sampleBooks.js";
import "../styles/main.css";

export default function Home() {
  const [books, setBooks] = useState([]);
  const user = JSON.parse(localStorage.getItem("user"));
  const navigate = useNavigate(); // hook for navigation

  useEffect(() => {
    setBooks(sampleBooks.slice(0, 20)); // Only first 20 books
  }, []);

  const handleProtectedClick = (bookId) => {
    if (!user) {
      alert("Please login to view this page");
    } else {
      navigate(`/book/${bookId}`); // navigate to book details
    }
  };

  return (
    <div className="home-page">
      {/* Banner */}
      <div className="home-banner">
        <div className="banner-overlay">
          <div className="banner-text">
            <h1>Welcome to Online Bookstore</h1>
            <p>Explore & Buy Your Favorite Books</p>
            <button
              className="shop-now-btn"
              onClick={() =>
                user
                  ? navigate("/books") // maybe a books listing page
                  : alert("Please login to view books")
              }
            >
              Shop Now
            </button>
          </div>
        </div>
      </div>

      {/* Featured Books */}
      <div className="container">
        <h2 className="section-title">Featured Books</h2>
        <div className="books-grid">
          {books.map((book) => (
            <div
              key={book._id}
              onClick={() => handleProtectedClick(book._id)}
              style={{ cursor: "pointer" }}
            >
              <BookCard book={book} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
