import { FaFacebookF, FaTwitter, FaInstagram, FaLinkedin } from "react-icons/fa";

export default function Footer() {
  return (
    <footer style={styles.footer}>
      <div style={styles.container}>
        {/* Quick Links */}
        <div style={styles.section}>
          <h4 style={styles.heading}>Quick Links</h4>
          <ul style={styles.list}>
            <li style={styles.listItem} onClick={() => window.location.href = "/"}>Home</li>
            <li style={styles.listItem} onClick={() => window.location.href = "/cart"}>Cart</li>
            <li style={styles.listItem} onClick={() => window.location.href = "/categories"}>Categories</li>
            <li style={styles.listItem} onClick={() => window.location.href = "/about"}>About Us</li>
            <li style={styles.listItem} onClick={() => window.location.href = "/contact"}>Contact</li>
          </ul>
        </div>

        {/* Contact Info */}
        <div style={styles.section}>
          <h4 style={styles.heading}>Contact Us</h4>
          <p style={styles.text}>üìç 123 Book Street, Chennai, India</p>
          <p style={styles.text}>üìß support@bookstore.com</p>
          <p style={styles.text}>üìû +91 12345 67890</p>
        </div>

        {/* Social Media */}
        <div style={styles.section}>
          <h4 style={styles.heading}>Follow Us</h4>
          <div style={styles.socialIcons}>
            <FaFacebookF style={styles.icon} />
            <FaTwitter style={styles.icon} />
            <FaInstagram style={styles.icon} />
            <FaLinkedin style={styles.icon} />
          </div>
        </div>
      </div>

      <div style={styles.copy}>
        &copy; {new Date().getFullYear()} BookStore. All rights reserved.
      </div>
    </footer>
  );
}

const styles = {
  footer: {
    backgroundColor: "#ff6f61",
    color: "#fff",
    padding: "40px 20px 20px 20px",
    fontFamily: "'Poppins', sans-serif",
  },
  container: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-between",
    gap: "20px",
    maxWidth: "1200px",
    margin: "0 auto",
  },
  section: {
    flex: "1 1 200px",
  },
  heading: {
    fontSize: "18px",
    marginBottom: "12px",
    fontWeight: 600,
    borderBottom: "2px solid rgba(255,255,255,0.3)",
    display: "inline-block",
    paddingBottom: "4px",
  },
  list: {
    listStyle: "none",
    padding: 0,
    margin: 0,
  },
  listItem: {
    marginBottom: "8px",
    cursor: "pointer",
    transition: "color 0.3s",
  },
  text: {
    margin: "5px 0",
    fontSize: "14px",
  },
  socialIcons: {
    display: "flex",
    gap: "12px",
    marginTop: "8px",
  },
  icon: {
    fontSize: "18px",
    cursor: "pointer",
    transition: "transform 0.3s, color 0.3s",
  },
  copy: {
    textAlign: "center",
    marginTop: "30px",
    fontSize: "14px",
    borderTop: "1px solid rgba(255,255,255,0.3)",
    paddingTop: "15px",
  },
};

// Hover effect for social icons
Object.assign(styles.icon, {
  ":hover": {
    color: "#ffd700",
    transform: "scale(1.2)",
  },
});
